export function logicalChain(defaultRet, fns) {
    return async () => {
        if (fns.length === 0) {
            return defaultRet;
        }
        const [fn] = fns;
        return fn().then((bool) => {
            if (bool === defaultRet) {
                // @ts-ignore
                return logicalChain(defaultRet, fns.slice(1))();
            }
            else {
                return !defaultRet;
            }
        });
    };
}
